import {
  ANIMATE_ALL,
  ANIMATE_NONE,
  ANIMATE_ONCHANGE,
  DEFAULT_ANIMATE,
} from '../index';

export const getAnimationMode = (mode: string): (boolean | string) => {
  switch (mode) {
    case ANIMATE_ALL: return true;
    case ANIMATE_NONE: return false;
    case ANIMATE_ONCHANGE: return 'onchange';
    default: return (DEFAULT_ANIMATE === 'all');
  }
};
