import { arrayOf, number, object, shape, string, bool } from 'prop-types';

export const defaultAreas = undefined;

const dataShape = shape({
  id: string.isRequired,
  value: number,
});

const areaData = arrayOf(dataShape);

// Individual area graph
const areasDataShape = shape({
  id: string.isRequired,
  style: object,
  data: areaData,
});

const areasData = arrayOf(areasDataShape);

// Collection of area graphs
const areas = shape({
  id: string.isRequired,
  group: bool,
  data: areasData,
});

export const areasShape = arrayOf(areas);
