import { arrayOf, number, shape, string, oneOfType, object } from 'prop-types';

export const defaultBars = undefined;

const valueShape = shape({
  style: string,
  value: oneOfType([
    number,
    shape({
      value: number,
      theme: object,
    }),
  ]),
});

const groupShape = shape({
  offset: number,
  values: arrayOf(valueShape),
});

const dataShape = shape({
  id: string.isRequired,
  group: arrayOf(groupShape),
  values: arrayOf(valueShape),
  value: oneOfType([number, valueShape]),
});

const data = arrayOf(dataShape);

export const barsShape = data;
