import { TermsAndConditions } from '@buffet/user-agreement';
import React from 'react';
import { FormattedMessage } from 'react-intl';

import './Terms.scss';

interface TermsProps {
  terms: TermsAndConditions;
  showTitle?: boolean
}

export default ({ terms, showTitle = true }: TermsProps) => (
  <section id="agreement-terms" className="terms-and-conditions">
    {showTitle && <FormattedMessage id="agreement.terms" tagName="h1" />}
    <div className="terms-details" dangerouslySetInnerHTML={{ __html: terms.content }} />
  </section>
);
