// tslint:disable: only-arrow-functions

describe('Open all the top level pages of Bistro', () => {

  beforeEach(function() {
    cy.fixture(`credentials.json`)
      .then(credentials => {
        cy.login(credentials.username, credentials.password);
      });
  });

  it('Open Restaurant search', () => {
    cy.visit('/admin/bistro/restaurants');
    cy.get('header')
      .find('h1')
      .should('contain', 'Search for a restaurant');
  });

  it('Open Group search', () => {
    cy.visit('/admin/bistro/groups');
    cy.get('header')
      .find('h1')
      .should('contain', 'Search for a group');
  });

  it('Open User search', () => {
    cy.visit('/admin/bistro/users');
    cy.get('header')
      .find('h1')
      .should('contain', 'Search for users');
  });

  it('Open Permissions', () => {
    cy.visit('/admin/bistro/permissions');
    cy.get('header')
      .find('h1')
      .should('contain', 'Permissions and Access Type management');
  });

});

