import React from 'react';
import { FormRenderProps } from 'react-final-form';

import { Button } from '@buffet/button';

import strings from '../../strings';

import './SaveUser.scss';

interface SaveUserProps {
  inProgress: boolean;
  onSubmit: () => void;
  onCancel: () => void;
}

export const SaveUser = ({inProgress, onCancel, onSubmit}: SaveUserProps) => {
  function handleSave(e: React.MouseEvent<HTMLElement>) {
    onSubmit();
  }

  function handleCancel(e: React.MouseEvent<HTMLElement>) {
    onCancel();
  }

  return (
    <div className="header-actions">
      <Button theme="default" type="button" onClick={handleCancel} disabled={inProgress}>
        {strings['users.edit.cancel']}
      </Button>
      <Button theme="publish" htmltype="submit" type="button" onClick={handleSave} disabled={inProgress}>
        {strings['users.edit.save']}
      </Button>
    </div>
  )
};
