import * as React from 'react';

import { AsyncProgress, AsyncStatus } from 'shared-react-utils/useAsync';

import LoadingIcon from '../LoadingIcon/LoadingIcon';

import './DataDisplay.scss';

interface DataDisplayProps {
  status: AsyncStatus;
}

export default class DataDisplay extends React.Component<DataDisplayProps, any> {

  public renderFailed(err?: Error) {
    return (
      <div className="data-container">
        <p>Couldn't fetch this for you, sorry.</p>
        <p>{err ? err.message : 'Unknown Error'}</p>
      </div>
    );
  }

  public renderStarted() {
    return (
      <div className="data-container">
        <LoadingIcon />
      </div>
    );
  }

  public render() {
    switch (this.props.status.progress) {
      case AsyncProgress.Started:
        return this.renderStarted();
      case AsyncProgress.Failed:
        return this.renderFailed(this.props.status.error);
      case AsyncProgress.Done:
        return this.props.children;
      case AsyncProgress.Init:
      default:
        return (null);
    }
  }
}
