import React, { ReactNode } from 'react';
import { FormattedMessage } from 'react-intl';

const formatCountMessage = (messagePrefix: string, count: number): ReactNode | null => {
  return count > 0 ? (
    <FormattedMessage
      id={`guestcampaigns.custom_promotions.form.blocked_days.${messagePrefix}.${count === 1 ? '1' : 'n'}`}
      values={count !== 1 ? { count } : undefined}
    />
  ) : null;
};

export default formatCountMessage;
