import React, { SFC } from 'react';
import { times, timesOrdering } from './constants';
import Time from './Time';

const Times: SFC = () => (
  <div className="times">
    <ul>
      {timesOrdering.map((key: string) => (
        <Time key={key} short={times[key].short} time={times[key].time} name={key} />
      ))}
    </ul>
  </div>
);

export default Times;
