import classnames from 'classnames';
import React, { SFC } from 'react';

import { Button } from 'gca-react-components';
import './ModalHeader.scss';

interface Props {
  onClose: () => void;
  title: string;
  className?: string;
}

const ModalHeader: SFC<Props> = (props: Props) => {
  const { className, title, onClose } = props;

  /* eslint-disable jsx-a11y/no-static-element-interactions */
  return (
    <header className={classnames('pi-modal-header', className)}>
      <Button theme="link" onClick={onClose}>
        <svg xmlns="http://www.w3.org/2000/svg" height="19" width="19" viewBox="0 0 19.6 19.3">
          <path fill="none" stroke="#ACB1BE" strokeWidth="1.1704" d="M1.3 1.3, l16 16m0-16l-16 16" />
        </svg>
      </Button>
      <h1>{title}</h1>
    </header>
  );
};

export default ModalHeader;
