import { shallow } from 'enzyme';
import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';

import imgReports from '../assets/card_reports.png';
import imgReservations from '../assets/card_reservations.png';
import imgAccounts from '../assets/card_userAccounts.png';
import testImage from '../assets/test.png';
import { PureAppCard as AppCard } from './AppCard';

describe('<AppCard />', () => {
  it('should display generic card, in enabled state', () => {
    const cardApp = {
      enabled: true,
      name: 'test',
      url: '',
    };
    const cardImg = testImage;
    const wrapper = shallowWithIntl(<AppCard image={cardImg} app={cardApp} />);
    expect(wrapper).toMatchSnapshot();
  });

  it('should display generic card, in disabled state', () => {
    const cardApp = {
      enabled: false,
      name: 'test',
      url: '',
    };
    const cardImg = testImage;
    const wrapper = shallowWithIntl(<AppCard image={cardImg} app={cardApp} />);
    expect(wrapper).toMatchSnapshot();
  });

  it('should display reservations card', () => {
    const cardApp = {
      enabled: true,
      name: 'reservations',
      url: '',
    };
    const cardImg = imgReservations;
    const wrapper = shallowWithIntl(<AppCard image={cardImg} app={cardApp} />);
    expect(wrapper).toMatchSnapshot();
  });

  it('should display reports card', () => {
    const cardApp = {
      enabled: true,
      name: 'reports',
      url: '',
    };
    const cardImg = imgReports;
    const wrapper = shallowWithIntl(<AppCard image={cardImg} app={cardApp} />);
    expect(wrapper).toMatchSnapshot();
  });

  it('should display userAccounts card', () => {
    const cardApp = {
      enabled: true,
      name: 'userAccounts',
      url: '',
    };
    const cardImg = imgAccounts;
    const wrapper = shallowWithIntl(<AppCard image={cardImg} app={cardApp} />);
    expect(wrapper).toMatchSnapshot();
  });
});
