import { CurrentRestaurantConsumer } from '@buffet/current-restaurant-context';
import { IntlProvider as BuffetIntlProvider, registerMessages } from '@buffet/intl';
import ErrorBoundary from './ErrorBoundary/ErrorBoundary';


import React from 'react';
import ReactDOM from 'react-dom';

import { translationMessages } from './translations/i18n';

import GroupHome from './GroupHome/GroupHome';

import './app.scss';
registerMessages(translationMessages);

const render = () => {
  ReactDOM.render((
    <BuffetIntlProvider>
      <ErrorBoundary>
        <GroupHome/>
      </ErrorBoundary>
    </BuffetIntlProvider>

  ),
    document.getElementById('app'),
  );
};

render();
