// tslint:disable: only-arrow-functions

describe('Navigate to all routes of Login app', function() {

  beforeEach(() => {
    cy.fixture(`credentials.json`)
      .then(credentials => {
        cy.login(credentials.username, credentials.password);
      });
  });

  it('Open Login', () => {
    cy.visit('/login-v2');
    cy.get('[data-testid=user-name-text-input]').should('exist');
    cy.url().should('contain','/login-v2')
  });

  it('Open Login / Forgot Password', () => {
    cy.visit('/login-v2/forgot-password');
    cy.get('[data-testid=email-text-input]').should('exist');
    cy.url().should('contain','/login-v2/forgot-password')
  });

  it('Open Login / Reset Password', () => {
    cy.visit('/login-v2/reset-password');
    cy.get('[data-testid=email-text-input]').should('exist');
    cy.url().should('contain','/login-v2/reset-password')
  });

  xit('Open Login / Change Password', () => {
    cy.visit('/login-v2/change-password/1234');
    cy.get('[data-testid=email-text-input]').should('exist');
    cy.url().should('contain','/login-v2/change-password')
  });

  it('Open Login / Register Phone', () => {
    cy.visit('/login-v2/register-phone');
    cy.get('[data-testid=phone-input]').should('exist');
    cy.get('[data-testid=submit-button]').should('exist');
    cy.url().should('contain','/login-v2/register-phone')
  });

  xit('Open Login / Verify Code', () => {
    cy.visit('/login-v2/verify-code');
    cy.get('[data-testid=verify-code-input]').should('exist');
    cy.url().should('contain','/login-v2/verify-code')
  });
});
