import * as React from 'react';
import { RestaurantPromo } from '../../store/promos/promos.types';
import PromoCard from './PromoCard/PromoCard';

interface Props {
  promos: RestaurantPromo[];
}

function PromoList(props: Props) {
  return <div className="promo-list">{props.promos.map(promo => <PromoCard promo={promo} key={promo.id} />)}</div>;
}

export default PromoList;
