import React, { SFC } from 'react';
import { EndDateType } from '../../../store/campaigns/campaigns.types';
import renderDateRange from '../helpers/render-date-range';
import DetailLine, { DetailLineCommonProps } from './DetailLine';

interface ValueProps {
  start: string;
  end: string;
  endDateType?: EndDateType;
}

type Props = ValueProps & DetailLineCommonProps;

const DateRange: SFC<Props> = ({ start, end, endDateType, ...rest }) => (
  <DetailLine value={renderDateRange(start, end, endDateType)} {...rest} />
);

export default DateRange;
