import React, { ComponentType, ReactNode, SFC } from 'react';
import { FormattedMessage } from 'react-intl';

export interface DetailLineCommonProps {
  messageIdSuffix: string;
  nameContainer?: any;
  valueContainer?: string | ComponentType;
  valueClass?: string;
}

interface OwnProps {
  value: ReactNode;
}

type Props = DetailLineCommonProps & OwnProps;

const DetailLine: SFC<Props> = ({
  messageIdSuffix,
  nameContainer = 'h4',
  valueContainer: ValueContainer = 'p',
  valueClass,
  value,
}) => (
  <>
    <FormattedMessage id={`guestcampaigns.list.${messageIdSuffix}`} tagName={nameContainer} />
    <ValueContainer {...(valueClass ? { className: valueClass } : {})}>{value}</ValueContainer>
  </>
);

export default DetailLine;
