import React from 'react';
import { FormattedMessage } from 'react-intl';

const renderIncentiveDetails = ({ name, description, noteToDiners }: any): JSX.Element[] => {
  const incentiveDetails = [
    <>
      <FormattedMessage id="guestcampaigns.list.incentive.title" tagName="h4" />
      <p>{name}</p>
    </>,
    <>
      <FormattedMessage id="guestcampaigns.list.incentive.description" tagName="h4" />
      <p>{description}</p>
    </>,
  ];

  if (noteToDiners) {
    incentiveDetails.push(
      <>
        <FormattedMessage id="guestcampaigns.list.incentive.terms" tagName="h4" />
        <p>{noteToDiners}</p>
      </>,
    );
  }

  return incentiveDetails;
};

export default renderIncentiveDetails;
