import { IntlProvider } from 'react-intl';

import { keys } from '../../../translations/bundles/marketing.json';
import makeDaysAndTimes from './makeDaysAndTimes';

describe('makeDaysAndTimes()', () => {
  it('should make days and times', () => {
    const intlProvider = new IntlProvider({ locale: 'en', messages: keys }, {});
    const intl = intlProvider.getChildContext().intl;
    const schedule = [
      { startTime: '12:00:00', endTime: '21:00:00', dayOfWeek: 'SUNDAY' },
      { startTime: '09:00:00', endTime: '10:00:00', dayOfWeek: 'MONDAY' },
    ];

    expect(makeDaysAndTimes(schedule, intl)).toEqual('Mon: 9:00 AM - 10:00 AM, Sun: 12:00 PM - 9:00 PM');
  });
});
