import percentChangeString from './percentChangeString';

describe('percentChangeString()', () => {
  it('should display percent increase or decrease as a string', () => {
    expect(percentChangeString(0, 0)).toEqual('');
    expect(percentChangeString(0, 1)).toEqual('');
    expect(percentChangeString(1, 0)).toEqual('');
    expect(percentChangeString(1, 1)).toEqual('');
    expect(percentChangeString(1, 2)).toEqual('(100%)');
    expect(percentChangeString(2, 1)).toEqual('(50%)');
    expect(percentChangeString(1, 3)).toEqual('(200%)');
    expect(percentChangeString(3, 1)).toEqual('(67%)');
    expect(percentChangeString(20, 100)).toEqual('(400%)');
    expect(percentChangeString(234, 1)).toEqual('(100%)');
    expect(percentChangeString(23423423423423, 1)).toEqual('(100%)');
    expect(percentChangeString(1, 999999)).toEqual('(999+%)');
    expect(percentChangeString(1000, 999)).toEqual('(0.1%)');
    expect(percentChangeString(999, 1000)).toEqual('(0.1%)');
  });
});
