import moment from 'moment';

const predefinedDateRangeOptions = [
  {
    lastWeek: [
      moment()
        .subtract(1, 'week')
        .startOf('week'),
      moment()
        .subtract(1, 'week')
        .endOf('week'),
    ],
  },
  {
    lastMonth: [
      moment()
        .subtract(1, 'month')
        .startOf('month'),
      moment()
        .subtract(1, 'month')
        .endOf('month'),
    ],
  },
  {
    last30Days: [moment().subtract(29, 'days')],
  },
  {
    yearToDate: [moment().startOf('year')],
  },
  {
    thisYear: [moment().startOf('year'), moment().endOf('year')],
  },
];

export default predefinedDateRangeOptions;
