import moment from 'moment';
import { DefaultSuppressionDateDescription } from '../../store/campaigns/campaigns.types';
import { dateFormat } from '../constants';
import defaultSuppressionDates from '../default-suppression-dates.json';

/* Not i18n */
const blockedDays = defaultSuppressionDates.reduce(
  (accumulator: any, currentValue: DefaultSuppressionDateDescription) => {
    accumulator.default[currentValue.id] = true;
    return accumulator;
  },
  { custom: {}, default: {} },
);

const initialValues = {
  'blocked-days': blockedDays,
  daysAndTimes: [
    {
      days: ['MONDAY', 'TUESDAY', 'WEDNESDAY', 'THURSDAY'],
      times: {
        end: '18:00',
        start: '16:00',
      },
    },
  ],
  startDate: moment()
    .add(1, 'week')
    .format(dateFormat.MOMENT),
};

export default initialValues;
