import * as React from 'react';
import { FormattedMessage } from 'react-intl';

import { LeadGenType } from '../../store/leadgen/leadgen.types';

import './LeadGenRequested.scss';

const privateDiningLink = 'https://restaurant.opentable.com/products/extras/private-dining';

interface Props {
  type: LeadGenType;
}

export default function LeadGenRequested(props: Props) {
  const here = (
    <a href={privateDiningLink} target="_blank">
      <FormattedMessage id="leadgen.here" />
    </a>
  );

  const message =
    props.type === LeadGenType.PrivateDining ? (
      <FormattedMessage tagName="p" id="leadgen.submitted.message" values={{ here }} />
    ) : (
      <FormattedMessage tagName="p" id="leadgen.submitted.simpleMessage_24" />
    );

  return (
    <div className="leadgen-requested">
      <FormattedMessage tagName="h3" id="leadgen.submitted.title" />
      {message}
    </div>
  );
}
