import React from 'react';
import ToastSteps from './ToastSteps';
import ToastWaiting from './ToastWaiting';

interface ToastContainerState {
  isToastStepsCompleted: boolean;
}

class ToastContainer extends React.Component<{}, ToastContainerState> {
  public state = {
    isToastStepsCompleted: false,
  };

  public render() {
    return this.state.isToastStepsCompleted ? <ToastWaiting /> : <ToastSteps onDone={this.handleToastStepsDone} />;
  }

  private handleToastStepsDone = () => {
    this.setState({ isToastStepsCompleted: true });
  };
}

export default ToastContainer;
