import { Button } from '@buffet/button';
import React from 'react';
import { Field } from 'react-final-form';
import { FormattedMessage } from 'react-intl';
import { UnsupportedPosBrand } from '../store/types';

import './LeadGenForm.scss';

export interface Props {
  handleSubmit: () => void;
  unsupportedPosBrands: UnsupportedPosBrand[];
}

export const LeadGenForm = ({ handleSubmit, unsupportedPosBrands }: Props) => (
  <form className="unsupported__lead-gen-form" onSubmit={handleSubmit}>
    <fieldset>
      <label htmlFor="lead-gen__first-name">First Name</label>
      <Field component="input" id="lead-gen__first-name" name="FirstName" placeholder="First Name" type="text" />
    </fieldset>
    <fieldset>
      <label htmlFor="lead-gen__last-name">Last Name</label>
      <Field component="input" id="lead-gen__last-name" name="LastName" placeholder="Last Name" type="text" />
    </fieldset>
    <fieldset>
      <label htmlFor="lead-gen__email">Email Address</label>
      <Field component="input" id="lead-gen__email" name="Email" placeholder="Email Address" type="text" />
    </fieldset>
    <fieldset>
      <label htmlFor="lead-gen__phone">Phone</label>
      <Field component="input" id="lead-gen__phone" name="Phone" placeholder="Phone" type="text" />
    </fieldset>
    <fieldset>
      <label htmlFor="lead-gen__company">Company Name</label>
      <Field component="input" id="lead-gen__company" name="Company" placeholder="Company Name" type="text" />
    </fieldset>
    <fieldset>
      <label htmlFor="lead-gen__unsupported-pos">Which POS do you use?</label>
      <Field component="select" id="lead-gen__unsupported-pos" name="POS_Brand__c" placeholder="Which POS do you use?">
        {unsupportedPosBrands.map(brand => (
          <option key={brand.Id} value={brand.Id}>
            {brand.Name}
          </option>
        ))}
      </Field>
    </fieldset>

    <Button type="button" onClick={() => null} htmltype="submit" className="unsupported-pos__submit">
      <FormattedMessage id="unsupportedPos.submit" />
    </Button>
  </form>
);
