import React from 'react';
import { FormattedMessage, InjectedIntlProps, injectIntl } from 'react-intl';

import { ModalWrapper } from 'gca-react-components';

interface DeleteReportModalProps {
  closeModal(): void;
  confirmCloseModal(): void;
}

interface Props extends DeleteReportModalProps, InjectedIntlProps {}

function DeleteReportModal(props: Props) {
  const { intl, confirmCloseModal, closeModal } = props;
  return (
    <ModalWrapper
      title={intl.formatMessage({ id: 'modals.deleteReport.title' })}
      confirmButtonText={intl.formatMessage({ id: 'modals.button.confirm' })}
      onConfirm={confirmCloseModal}
      cancelButtonText={intl.formatMessage({ id: 'modals.button.cancel' })}
      onCancel={closeModal}
      includeCancelButton
    >
      <FormattedMessage tagName="p" id="modals.deleteReport.body" />
    </ModalWrapper>
  );
}

export default injectIntl<DeleteReportModalProps>(DeleteReportModal);
export { DeleteReportModal as PureDeleteReportModal };
