import { LikeIcon, StarRating } from 'gca-react-components';
import React from 'react';
import { FormattedMessage, FormattedNumber } from 'react-intl';
import './Overall.scss';

interface Props {
  overallRating: number;
  ratingPeriod: number;
  dinersRecommend: number;
}

function Overall(props: Props) {
  return (
    <section className="summary-overall">
      <h1>
        <FormattedMessage id="reviews.summary.overall" />
      </h1>

      <p className="rating">
        <FormattedNumber value={props.overallRating} minimumSignificantDigits={1} maximumSignificantDigits={1} />
      </p>

      <StarRating rating={props.overallRating} size={25} />

      <p className="rating-period">
        <FormattedMessage id="reviews.summary.basedOn" values={{ ratingPeriod: props.ratingPeriod }} />
      </p>

      <p className="diners-recommend">
        <LikeIcon height="18" />
        <FormattedMessage
          id="reviews.summary.dinersRecommend"
          values={{ dinersRecommend: Math.round(props.dinersRecommend).toFixed(0) }}
        />
      </p>
    </section>
  );
}

export default Overall;
