import React from 'react';
import { FormattedMessage } from 'react-intl';

import { eventTracker, trackingCategory, trackingEvents } from '../../helpers/tracking';

const trackLink = () => {
  eventTracker.sendEvent({
    action: trackingEvents.marketingPage.action,
    category: trackingCategory,
    label: trackingEvents.marketingPage.labels.weCanHelp,
  });
};

export default () => (
  <section className="section footer">
    <FormattedMessage
      id="venga.marketing.footer"
      values={{
        helpLink: (
          <a href="https://restaurant.opentable.com/solutions/venga-help" target="_blank" onClick={trackLink}>
            <FormattedMessage id="venga.marketing.footer.helpLink" />
          </a>
        ),
      }}
    />
  </section>
);
