import { pathOr, propOr } from 'ramda';

type ScopeType = 'restaurant' | 'group' | 'none';

export interface GcScope {
  type: ScopeType;
  id?: number | string;
};

export const getGcScope: (c: any) => GcScope = propOr<GcScope>({ type: 'none'}, 'gcScope');

export const getScopeType = pathOr<ScopeType>('none', ['gcScope', 'type']);
