// Important modules this config uses
const { join } = require('path');
const path = require('path');
const webpack = require('webpack');
const pkg = require(join(process.cwd(), 'package.json'));
const outputPath = join(process.cwd(), 'dist');

process.env.NODE_ENV = 'development';

module.exports = require('./webpack.base')({
  mode: 'development',
  entry: Object.keys(pkg.dependencies).filter(dep => !dep.includes('@types')),
  output: {
    filename: 'gcaCommon.dll.dev.js',
    path: outputPath,
    library: 'gcaCommon',
  },
  plugins: [new webpack.DllPlugin({ name: 'gcaCommon', path: join(outputPath, 'gcaCommon.dev.json') })],
});
