const _ = require('lodash');
const chalk = require('chalk');
const divider = require('./divider');

module.exports = (title, items) => {
  const mappedItems = _.map(items, (value, key) => {
    return `${String.prototype.padStart.call(key, 15)}: ${chalk.magenta(value)}`;
  }).join('\n');
  
  console.log(`
${chalk.bold(title)}${divider}
${mappedItems}${divider}
  `);
}


