const chalk = require('chalk');

const divider = chalk.gray('-----------------------------------------------------------');
const header = (text, options = {}) => {
  const topPadding = options.top ? '\n'.repeat(options.top) : ''
  const bottomPadding = options.bottom ? '\n'.repeat(options.bottom) : ''
  return `${topPadding}${divider}\n${chalk.magenta(text)}\n${divider}${bottomPadding}`
};

module.exports = {
  divider,
  header,
}