import React from 'react';
import { DropzoneProps, ImageFile as DropzoneImageFile } from 'react-dropzone';

export interface ErrorCodes {
  ImageWidthTooSmall: 'ImageWidthTooSmall',
  ImageHeightTooSmall: 'ImageHeightTooSmall',
  ImageWidthTooLarge: 'ImageWidthTooLarge',
  ImageHeightTooLarge: 'ImageHeightTooLarge',
  FileSizeTooLarge: 'FileSizeTooLarge',
  FileSizeTooSmall: 'FileSizeTooSmall',
  FileTypeIncorrect: 'FileTypeIncorrect',
}

export const errorCodes: ErrorCodes;

export type ErrorCode = keyof ErrorCodes;

export interface ImageFile extends DropzoneImageFile {
  errorCodes: ErrorCode[],
}

interface Props extends DropzoneProps {
  minWidth?: number;
  minHeight?: number;
  maxWidth?: number;
  maxHeight?: number;

  onDrop?(acceptedFiles: ImageFile[], rejectedFiles: ImageFile[]): void;
  onDropAccepted?(acceptedFiles: ImageFile[]): void;
  onDropRejected?(rejectedFiles: ImageFile[]): void;
}

declare class Dropzone extends React.Component<Props> {}

export default Dropzone;
