import { shallow } from 'enzyme';
import React from 'react';

import { LabelGroup } from './LabelGroup';

describe('<LabelGroup />', () => {
  it('Should render a basic LabelGroup', () => {
    expect(
      shallow(
        <LabelGroup label="Foo">
          <input id="foo" />
        </LabelGroup>,
      ),
    ).toMatchSnapshot();
  });

  it('Should use `id` as htmlFor if present on Input', () => {
    const htmlFor = 'foo';
    const wrapper = shallow(
      <LabelGroup label="Foo">
        <input id={htmlFor} />
      </LabelGroup>,
    );
    expect(wrapper.find('label').props().htmlFor).toEqual(htmlFor);
  });

  it('Should use `name` as htmlFor if `id` is not present in input.', () => {
    const htmlFor = 'foo';
    const wrapper = shallow(
      <LabelGroup label="Foo">
        <input name={htmlFor} />
      </LabelGroup>,
    );
    expect(wrapper.find('label').props().htmlFor).toEqual(htmlFor);
  });

  it('Should set `id` of input to `name` if `id` is not present', () => {
    const htmlFor = 'foo';
    const wrapper = shallow(
      <LabelGroup label="Foo">
        <input name={htmlFor} />
      </LabelGroup>,
    );
    expect(wrapper.find('input').props().id).toEqual(htmlFor);
  });
});
