import React from 'react';
import PropTypes from 'prop-types';

/**
 * Render the given Icon as child of a `NavLink` inside a `Nav` component
 * @param {*} props
 */
export function NavIcon({ children, title }) {
  return (
    <span className="gca-nav-icon" title={title}>
      {children}
    </span>
  );
}

NavIcon.propTypes = {
  children: PropTypes.node.isRequired,
  title: PropTypes.string,
};

export default NavIcon;
