import React from 'react';
import PropTypes from 'prop-types';

/**
 * Render a label within a `NavLink` or a `a` element inside a `Nav` component
 * @param {*} props
 */
export function NavLabel(props) {
  return <span className="gca-nav-label">{props.children}</span>;
}

NavLabel.propTypes = {
  children: PropTypes.node,
};

export default NavLabel;
