import React from 'react';
import PropTypes from 'prop-types';
import './Panel.scss';

export function Panel(props) {
  const defaultStyle = { 'flex': '1 1 auto' };
  const computedStyle = props.customHeaderStyle ? { ...defaultStyle, ...props.customHeaderStyle } : defaultStyle;
  const headerContent = props.headerContent
    ? <div className="header-content" style={computedStyle}>{props.headerContent}</div>
    : '';

  const title = props.title ? <h1>{props.title}</h1> : '';

  return (
    <section className="gca-panel">
      <header className="gca-panel-header">
        {title}
        {headerContent}
      </header>

      <div className="gca-panel-body">{React.Children.toArray(props.children)}</div>
    </section>
  );
}

Panel.propTypes = {
  children: PropTypes.node,
  headerContent: PropTypes.node,
  title: PropTypes.string,
  customHeaderStyle: PropTypes.objectOf(PropTypes.string),
};
