import PropTypes from 'prop-types';
import PriceSlider from './PriceSlider';
import customField from '../customField/customField';

/**
 * Wraps a [`<PriceSlider>`](#textarea) component in a Redux Form <a href="http://redux-form.com/6.6.3/docs/api/Field.md/" target="_blank">`<Field>`</a>.
 */
export const PriceSliderField = customField({
  component: PriceSlider,
  propTypes: {
    locale: PropTypes.string,
    disabled: PropTypes.bool,
  },
});

export default PriceSliderField;
