import * as React from 'react';

interface StandardShift {
  id: string,
  label: string,
}

interface BaseShift {
  days: string[];
  shiftStarts: string;
  shiftEnds: string;
  lastSeating?: string;
}

export interface CustomNameShift extends BaseShift {
 customNameShift: {
    name: string;
    locale: string;
 }
}

export interface StandardNameShift extends BaseShift {
  standardShiftName: {
    id: string;
  }
}

interface Labels {
  addShift: string;
  removeShift: string;
  shiftEnds: string;
  shiftStarts: string;
  timeRangeTo: string;
  lastSeating: string;
  invalidName: string;
  invalidTimeRange: string;
  invalidDays: string;
}

interface ShiftError {
  shiftName?: string,
  days?: string,
  timeRange?: string,
}

export interface Props {
  timeRangeSeparator?: string;
  standardShifts: StandardShift[];
  onShiftsChange?: ( shifts: StandardNameShift|CustomNameShift[] ) => void;
  onChange?: ( shifts: StandardNameShift|CustomNameShift[] ) => void;
  shifts?: StandardNameShift|CustomNameShift[];
  labels: Labels;
  errors: ShiftError[];
  disabled: boolean;
  locale: string;
  countryCode: string;
  valid?: boolean;
  forceDirty?: boolean;
}

export class ProfileHours extends React.Component<Props> {
}

export default ProfileHours;
