/**
 *
 * Table - gca table and styles
 *  props.className is optional, to allow caller to add own custom tabel class
 */

import React from 'react';
import PropTypes from 'prop-types';

import './Table.scss';

export function Table({ className = '', children }) {
  const classes = `gca-table ${className}`;
  return <table className={classes}>{React.Children.toArray(children)}</table>;
}

Table.propTypes = {
  className: PropTypes.string,
  children: PropTypes.node,
};
