
import * as React from 'react';
import * as moment from 'moment';
import { customFinalField } from './customfield';

import LocaleSelect, { Props as LocaleSelectProps } from '../components/LocaleSelect/LocaleSelect';

/**
 * LocaleSelectField react-final-form field for gca-react-components LocaleSelect
 */
export const LocaleSelectFinalField = customFinalField<LocaleSelectProps>(
  (  { disabled, options }, { input } ) => {
    return (
      <LocaleSelect
        disabled={disabled}
        options={options}
        value={input.value}
        onChange={input.onChange}
      />
    );
  }
);

/** @component */
export default LocaleSelectFinalField;