import * as React from 'react';
import * as moment from 'moment';

import { customFinalField } from './customfield';

import PriceSlider, { Props as SliderProps } from '../components/PriceSlider/PriceSlider';

/**
 * PriceSliderField react-final-form field for gca-react-components PriceSlider
 */
export const PriceSliderFinalField = customFinalField<SliderProps>(
  ( { value, locale, defaultValue, valid }, {input} ) => {

    return (
      <PriceSlider
        {...input}
        value={typeof input.value === 'object' ? input.value : {}}
        defaultValue={defaultValue}
        locale={locale}
        onChange={input.onChange}
        valid={valid}
      />
    );
  }
);

/** @component */
export default PriceSliderFinalField;
