import { shallow } from 'enzyme';
import * as React from 'react';

import { SimpleRadioButtonGroupFinalField } from './SimpleRadioButtonGroupFinalField';
import { RadioButton } from '../components/RadioButton/RadioButton';

describe('<SimpleRadioButtonGroupFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <SimpleRadioButtonGroupFinalField
          name="mytext"
        >
          <RadioButton key="1" value="first" label="First Option" />
          <RadioButton key="2" value="second" label="Second Option" />
        </SimpleRadioButtonGroupFinalField>
      )
    ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <SimpleRadioButtonGroupFinalField
          name="mytext"
          valid={false}
        >
          <RadioButton key="1" value="first" label="First Option" />
          <RadioButton key="2" value="second" label="Second Option" />
        </SimpleRadioButtonGroupFinalField>
      )
    ).toMatchSnapshot();
  });

});
