import * as React from 'react';
import * as moment from 'moment';

import { customFinalField } from './customfield';

import SwitchList, { Props as SwitchListProps } from '../components/SwitchList/SwitchList';

/**
 * SwitchListField is a react-final-form field around gca-react-components SwitchList
 */
export const SwitchListFinalField = customFinalField<SwitchListProps>(
  ( { className, errorMessage, options, valid }, { input } ) => {
    return (
      <SwitchList
        options={options}
        valid={valid}
        className={className}
        selected={input.value || []}
        errorMessage={errorMessage}
        {...input}
      />
    );
  }
);

/** @component */
export default SwitchListFinalField;
