import { shallow } from 'enzyme';
import * as React from 'react';

import { TextAreaFinalField } from './TextAreaFinalField';

describe('<TextAreaFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <TextAreaFinalField
          name="mytext"
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <TextAreaFinalField
          name="mytext"
          valid={false}
        />)
      ).toMatchSnapshot();
  });

});
