import { shallow } from 'enzyme';
import * as React from 'react';

import { TimeRangeFinalField } from './TimeRangeFinalField';

describe('<TimeRangeFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <TimeRangeFinalField
          name="mytime"
          firstSelect={{ increment: 60 }}
          secondSelect={{ increment: 60 }}
          locale="en-us"
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <TimeRangeFinalField
          name="mytime"
          firstSelect={{ increment: 60 }}
          secondSelect={{ increment: 60 }}
          locale="en-us"
          valid={false}
        />)
      ).toMatchSnapshot();
  });

  it('should render the field with more props', () => {
    expect(
      shallow(
        <TimeRangeFinalField
          name="mytime"
          firstSelect={{
            start: '10:00',
            startType: 'inclusive',
            end: '12:00',
            endType: 'inclusive',
            increment: 60
          }}
          secondSelect={{
            start: '11:00',
            startType: 'inclusive',
            end: '22:00',
            endType: 'inclusive',
            increment: 60
          }}
          labels={{
            start: 'Start',
            end: 'End',
            to: 'To',
            invalid: 'Test error message',
          }}
          locale="en-us"
        />)
      ).toMatchSnapshot();
  });

});
