import { shallow } from 'enzyme';
import * as React from 'react';

import { TimeSelectFinalField } from './TimeSelectFinalField';

describe('<TimeSelectFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <TimeSelectFinalField
          name="mytime"
          locale="en-us"
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <TimeSelectFinalField
          name="mytime"
          valid={false}
          locale="en-us"
        />)
      ).toMatchSnapshot();
  });

});
