import React from 'react';
import PropTypes from 'prop-types';
import dimensionWrapper from './dimensionWrapper';

const style = {
  fill: 'none',
  strokeLinecap: 'round',
  strokeLinejoin: 'round',
};

export const ClockIcon = dimensionWrapper(props => (
  <svg height={props.height} width={props.width} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 18 18">
    <title>clock icon</title>
    <circle style={{ ...style, stroke: props.color }} cx="9" cy="8.5" r="7.5" />
    <line style={{ ...style, stroke: props.color }} x1="8.7" y1="4.59" x2="8.7" y2="8.5" />
    <line style={{ ...style, stroke: props.color }} x1="8.84" y1="8.5" x2="11.45" y2="11.11" />
  </svg>
));

ClockIcon.propTypes = {
  height: PropTypes.string,
  width: PropTypes.string,
  color: PropTypes.string,
};

ClockIcon.defaultProps = {
  height: '25',
  color: '#FFF',
};
