#!/bin/bash

function exitIfError {
  if [ $? == "0" ]
    then
      printf "\n########### $1 #############\n"
    else
      explode "$2"
  fi
}

function explode {
  printf "\n################## FATAL: $1 #######################\n"
  exit 1
}


function tc_start {
  echo "##teamcity[blockOpened name='$1']"
}

function tc_end {
  echo "##teamcity[blockClosed name='$1']"
}

function tc_message {
  echo "##teamcity[message text='$1']"
}
