module.exports = function (SmartlingBaseOptions, SmartlingRetrievalTypes, SmartlingFileNameModes,
  SmartlingLocaleModes) {
  class SmartlingDownloadOptions extends SmartlingBaseOptions {
    constructor(props) {
      super(props);

      this.localeIds = props.localeIds || ['en-us'];

      this.retrievalType = props.retrievalType || SmartlingRetrievalTypes.published;

      this.fileNameMode = props.fileNameMode || SmartlingFileNameModes.trimLeading;

      this.localeMode = props.localMode || SmartlingLocaleModes.localeInPath;

      if (props.zipFileName) {
        this.zipFileName = props.zipFileName;
      }

      if (props.includeOriginalStrings !== undefined) {
        this.includeOriginalStrings = props.includeOriginalStrings;
      }
    }
  }

  return SmartlingDownloadOptions;
};
