import React from 'react';

import './Modal.scss';

interface ModalProps {
  show: boolean;
  onClose: (e: React.MouseEvent<HTMLElement>) => void;
  overlayClass?: string;
  modalClass?: string;
  children: React.ReactNode;
}

export function Modal({ onClose, overlayClass, modalClass, children, show }: ModalProps) {

  function handleClose(e: React.MouseEvent<HTMLDivElement>) {
    if (e.target === e.currentTarget) {
      onClose(e);
    }
  }
  const mainClass = `buffet-modal-overlay ${overlayClass || ''} ${show ? 'show' : 'hide'}`;
  return (
    <div onClick={handleClose} className={mainClass}>
      <div className={`buffet-modal ${modalClass || ''}`}>{children}</div>
    </div>
  );
}
